const formatDate = (iso?: string) => {
  if (!iso) return '';
  const [year, month, day] = iso.split('T')[0].split('-');
  return `${day}/${month}/${year}`;
};

export const buildInvoiceHTML = (invoice: Invoice, mode: 'invoice' | 'quotation' = 'invoice'): string => {
  let html = HTML_TEMPLATE;

  // 1. Client Details
  html = html.replace('{{CLIENT_NAME}}', invoice.client?.name || '');
  html = html.replace('{{CLIENT_PHONE}}', invoice.client?.phone || '');
  html = html.replace('{{INVOICE_ID}}', invoice.id);

  let rowsHtml = '';

  invoice.events.forEach(event => {
    rowsHtml += `
      <tr>
        <td colspan="2">
          <div style="font-weight: bold; font-size: 1.1em; margin-bottom: 8px;">
            ${event.eventType}
          </div>

          ${event.services
            .map(
              service => `
            <div style="display: flex; justify-content: space-between; margin-left: 12px; margin-bottom: 4px;">
              <div> ${service.name}</div>
              <div style="font-weight: bold;">${service.amount} </div>
            </div>
          `
            )
            .join('')}
        </td>
      </tr>
    `;
  });

  html = html.replace('{{EVENT_ROWS}}', rowsHtml);

  // 3. Totals
  html = html.replace('{{SUBTOTAL}}', invoice.subtotal.toFixed(2));
  html = html.replace('{{TOTAL}}', invoice.total.toFixed(2));

  // Advance row (optional)
  let advanceRow = '';

  if (invoice.client?.advance && invoice.client.advance > 0) {
    advanceRow = `
    <div class="totals-row">
      <span>Advance Paid:</span>
      <span>- ${invoice.client.advance.toFixed(2)} </span>
    </div>
  `;
}
html = html.replace('{{ADVANCE_ROW}}', advanceRow);


  html = html.replace('{{QR_IMAGE}}', QR_BASE64);

const isQuotation = invoice.mode === 'quotation';

/* Invoice / Quotation title */
html = html.replace(
  '<h2>Invoice</h2>',
  `<h2>${isQuotation ? 'Quotation' : 'Invoice'}</h2>`
);

/* Invoice meta block */
const invoiceMeta = isQuotation
  ? ''
  : `
    <p>Invoice #: <strong>${invoice.id}</strong></p>
    <p>Event Date: <strong>${formatDate(invoice.client?.eventDate)}</strong></p>
    <p>Date Issued: <strong>${formatDate(invoice.client?.dateIssued)}</strong></p>
  `;

html = html.replace('{{INVOICE_META}}', invoiceMeta);

/* QR block */
const qrBlock = isQuotation
  ? ''
  : `
    <div class="qr-code-placeholder">
      <img src="${QR_BASE64}" alt="Payment QR Code">
    </div>
  `;

html = html.replace('{{QR_BLOCK}}', qrBlock);


  return html;
};
